api.adaptor:
var rows = payload?.query_result?.data?.rows;
var legend_data = [], series_data = [], toperformthe_count = 0, sum_count = 0, fullfill_count=0;
rows.forEach((item) => {
  var count = item.count;
  sum_count = sum_count + count;
  if (item._id === 'notstart') {
    legend_data.push('未开始');
    series_data.push({ value: count, name: '未开始' });
  } else if (item._id === 'toperformthe') {
    toperformthe_count = count;
    legend_data.push('执行中');
    series_data.push({ value: count, name: '执行中' });
  } else if (item._id === 'fullfill') {
    fullfill_count = count;
    legend_data.push('结束');
    series_data.push({ value: count, name: '结束' });
  } else if (item._id === 'cancel') {
    legend_data.push('已取消');
    series_data.push({ value: count, name: '已取消' });
  }
})

payload.data = {
  sum_count,
  toperformthe_count,
  fullfill_count,
  legend_data,
  series_data
};

return payload;

